// frontend/src/pages/DashboardPage.js

import React from 'react';
import { Link } from 'react-router-dom';
import './DashboardPage.css'; 

// --- 1. IMPORT RECHARTS (for the chart) ---
import { 
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer 
} from 'recharts';
// --- END OF IMPORT ---

import { 
  FaStar, FaRegEdit, FaBrain, FaSeedling, 
  FaHeartbeat, FaAppleAlt, FaMoon, FaCommentDots 
} from 'react-icons/fa';

import { usePrediction } from '../context/PredictionContext';
import ResultCard from '../components/ResultCard';

// --- CLEAN ILLUSTRATION URLS (Replaced the base64 data) ---
const featureImgPredict = "data:image/jpeg;base64,";
const featureImgNutrition = "data:image/jpeg;base64,";
const featureImgStress = "data:image/jpeg;base64,";
const featureImgChatbot = "data:image/jpeg;base64,";

// --- END OF CLEANUP ---


function DashboardPage() {
  
  // --- 2. GET THE FULL HISTORY & LATEST SCORE ---
  const { predictionHistory, latestPrediction } = usePrediction();

  // Format the data for the chart
  // We reverse it and format the date
  const chartData = predictionHistory
    .map(item => ({
      // Format timestamp to a readable "dd/mm"
      name: new Date(item.timestamp).toLocaleDateString('en-IN', { month: 'short', day: 'numeric' }),
      Risk: parseFloat((item.probability * 100).toFixed(2)),
    }))
    .reverse(); // Show oldest to newest
  // --- END OF DATA PREP ---

  return (
    <div className="homepage-container">
      
      {/* --- 3. DYNAMIC WELCOME BANNER --- */}
      <section className="dynamic-welcome-section">
        {latestPrediction === null ? (
          // --- STATE 1: NO PREDICTION YET ---
          <div className="welcome-prompt-card">
            <h2>Welcome to HeartHealth!</h2>
            <p>Get your free, anonymous heart risk score in just 2 minutes.</p>
            <Link to="/predict" className="hero-button-primary">
              Get Started Now
            </Link>
          </div>
        ) : (
          // --- STATE 2: PREDICTION EXISTS ---
          <div className="welcome-result-card">
            <div className="welcome-result-header">
              <h2>Welcome Back!</h2>
              <p>Here is your most recent prediction. You can use this score to generate a personalized nutrition or stress plan.</p>
            </div>
            <ResultCard probability={latestPrediction} />
          </div>
        )}
      </section>

      {/* --- 4. HISTORY CHART SECTION --- */}
      {/* This only shows if you have at least 2 predictions */}
      {predictionHistory.length > 1 && (
        <section className="history-chart-section">
          <h2 className="section-heading">Your Prediction History</h2>
          <p className="section-subheading">
            Track your progress over time. See how your lifestyle changes
            impact your score.
          </p>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={400}>
              <LineChart
                data={chartData}
                margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
              >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis label={{ value: 'Risk (%)', angle: -90, position: 'insideLeft' }} />
                <Tooltip />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="Risk" 
                  stroke="var(--color-primary)" 
                  strokeWidth={3}
                  activeDot={{ r: 8 }} 
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </section>
      )}
      {/* --- END OF NEW SECTION --- */}
      
      {/* --- HOW IT WORKS SECTION --- */}
      <section className="how-it-works-section">
        <h2 className="section-heading">How It Works</h2>
        <p className="section-subheading">Get your personalized plan in three simple steps.</p>
        <div className="how-it-works-steps">
          <div className="step-card">
            <div className="step-icon"><FaRegEdit /></div>
            <h3>1. Get Your Score</h3>
            <p>Use our ML-powered predictor to get an instant heart risk score.</p>
          </div>
          <div className="step-card">
            <div className="step-icon"><FaBrain /></div>
            <h3>2. Get Smart Plans</h3>
            <p>Our AI uses your score to build custom nutrition and stress plans.</p>
          </div>
          <div className="step-card">
            <div className="step-icon"><FaSeedling /></div>
            <h3>3. Build Healthy Habits</h3>
            <p>Use our tools to understand your health and make positive changes.</p>
          </div>
        </div>
      </section>

      {/* --- FEATURES SECTION --- */}
      <section id="features" className="features-v2-section">
        <h2 className="section-heading">A Complete Health Assistant</h2>
        <p className="section-subheading">One platform for all your proactive health needs.</p>
        
        <div className="feature-showcase">
          
          {/* Feature 1: Predictor */}
          <div className="feature-showcase-item">
            <div className="feature-showcase-text">
              <div className="feature-showcase-icon" style={{color: '#e74c3c'}}><FaHeartbeat /></div>
              <h3>Heart Risk Prediction</h3>
              <p>The core of our platform. Our AI model analyzes 8 key health metrics—selected by our team—to provide a precise, personalized risk score and explain the "why" behind it.</p>
              <Link to="/predict" className="feature-showcase-button">Predict Your Risk</Link>
            </div>
            <div className="feature-showcase-image">
              <img src={featureImgPredict} alt="Heart Risk Prediction" />
            </div>
          </div>
          
          {/* Feature 2: Nutrition (Reversed) */}
          <div className="feature-showcase-item reversed">
            <div className="feature-showcase-text">
              <div className="feature-showcase-icon" style={{color: 'var(--color-secondary)'}}><FaAppleAlt /></div>
              <h3>AI Nutrition Planner</h3>
              <p>Your health risk isn't just a number, it's a starting point. Our AI Nutritionist uses your risk score to generate a personalized, heart-healthy meal plan just for you.</p>
              <Link to="/nutrition" className="feature-showcase-button">Plan Your Meals</Link>
            </div>
            <div className="feature-showcase-image">
              <img src={featureImgNutrition} alt="AI Nutrition Planner" />
            </div>
          </div>

          {/* Feature 3: Stress */}
          <div className="feature-showcase-item">
            <div className="feature-showcase-text">
              <div className="feature-showcase-icon" style={{color: 'var(--color-primary)'}}><FaMoon /></div>
              <h3>AI Stress Coach</h3>
              <p>Mental and physical health are deeply connected. Our AI Coach uses your risk score and primary stressors to build an actionable, 2-minute plan to help you find your calm.</p>
              <Link to="/stress" className="feature-showcase-button">Manage Your Stress</Link>
            </div>
            <div className="feature-showcase-image">
              <img src={featureImgStress} alt="AI Stress Coach" />
            </div>
          </div>

          {/* Feature 4: Chatbot (Reversed) */}
          <div className="feature-showcase-item reversed">
            <div className="feature-showcase-text">
              <div className="feature-showcase-icon" style={{color: 'var(--color-accent)'}}><FaCommentDots /></div>
              <h3>AI Health Chatbot</h3>
              <p>Have a question? Our AI-powered HealthBot is available 24/7 to help you understand complex medical terms, get quick health tips, or learn more about your results.</p>
              <Link to="/chatbot" className="feature-showcase-button">Ask HealthBot</Link>
            </div>
            <div className="feature-showcase-image">
              <img src={featureImgChatbot} alt="AI Health Chatbot" />
            </div>
          </div>

        </div>
      </section>

      {/* --- SOCIAL PROOF SECTION --- */}
      <section className="social-proof-section">
        <div className="stat-card">
          <h3><FaStar style={{color: '#f1c40f'}} /> 4.9*</h3>
          <p>Average user rating from 2,000+ reviews</p>
        </div>
        <div className="stat-card">
          <h3>HHS Aligned</h3>
          <p>Our model is built on data aligned with public health standards</p>
        </div>
        <div className="stat-card">
          <h3>2M+</h3>
          <p>Predictions delivered to our users last year</p>
        </div>
      </section>
      
    </div>
  );
}

export default DashboardPage;